mapper.insert:

    SELECT NOTICE_SEQ.NEXTVAL
      FROM DUAL

INSERT INTO NOTICE_T(
    NOTICE_NO
  , TITLE
  , CONTENTS
  , HIT
  , CREATED_AT
) VALUES (
    #{noticeNo}
  , #{title}
  , #{contents}
  , NVL(#{hit}, 0) 
  , TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS') 
)

mapper.select:
SELECT NOTICE_NO, TITLE, CONTENTS, HIT, CREATED_AT
  FROM NOTICE_T
 WHERE NOTICE_NO = #{noticeNo}

mapper.update:
UPDATE NOTICE_T
SET TITLE = #{title},
CONTENTS = #{contents}
WHERE NOTICE_NO = #{noticeNo}
